    <h1>Salle index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Nom</th>
                <th>Siege</th>
                <th>Caracteristique</th>
                <th>Probleme</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for salle in salles %}
            <tr>
                <td>{{ salle.id }}</td>
                <td>{{ salle.Nom }}</td>
                <td>{{ salle.Siege }}</td>
                <td>{{ salle.Caracteristique }}</td>
                <td>{{ salle.Probleme }}</td>
                <td>
                    <a href=\"{{ path('app_salle_show', {'id': salle.id}) }}\">show</a>
                    {% if is_granted('ROLE_ADMIN') %}
                        <a href=\"{{ path('app_salle_edit', {'id': salle.id}) }}\">edit</a>
                    {% endif %}
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_salle_new') }}\">Create new</a>
{% endblock %}
